import React, { useState, useEffect } from "react";
import {
  StyleSheet,
  FlatList,
  View,
  Text,
  TouchableOpacity,
} from "react-native";
import store from "./store";
import "../../global";

const History = (props) => {

  var jarName = props.route.params.key;

  const [arrayData, setarrayData] = useState("");
  const [dateTimeArr, setdateTimeArr] = useState("");
  const [loading, setloading] = useState(true);
  const [edit, setedit] = useState(false);

  store.subscribe(() => {
    jarName = props.route.params.key;
    fetchData();
  })
  useEffect(() => fetchData(), []);

  const fetchData = () => {
    setloading(true);
    fetch(`${global.ipAddress}:3001/history/` + jarName)
      .then((res) => res.json())
      .then((resJson) => {
        console.log(resJson);
        var values = resJson.status;
        setarrayData(...arrayData, { value: values, dateTime: new Date() });
        // this.setState({
        //   arrayData: [
        //     ...this.state.arrayData,
        //     { value: values, dateTime: new Date() },
        //   ],
        // });
        setloading(false);
      })
      .catch((e) => console.log(e));
  };

  const getValues = (val) => {
    if (val >= 0 && val <= 10) {
      return <Text>high</Text>;
    } else if (val >= 11 && val <= 16) {
      return <Text>Medium</Text>;
    } else if (val >= 17) {
      return <Text>low</Text>;
    }
  };

  const getDateTime = (dt) => {
    const dateTime = dt.toUTCString();
    return <Text style={styles.dateStyle}>{dateTime}</Text>;
  };

  const handleRefresh = () => {
    setloading(false);
    fetchData();
    setloading(true);
  };

  const renderSeparator = () => (
    <View
      style={{
        marginBottom: -10,
      }}
    />
  );

  return (
    <FlatList
      data={arrayData}
      renderItem={({ item }) => (
        <TouchableOpacity style={styles.card}>
          <View style={styles.jarNameContainer}>
            <Text style={styles.jarText}>{jarName}</Text>
            {this.getDateTime(item.dateTime)}
            <View style={styles.pill}>{getValues(item.value)}</View>
          </View>
        </TouchableOpacity>
      )}
      ItemSeparatorComponent={renderSeparator}
      keyExtractor={(item, index) => index.toString()}
      refreshing={loading}
      onRefresh={handleRefresh}
    />
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: "center",
    backgroundColor: "#f2f2f2",
    width: "100%",
    height: "100%",
  },
  card: {
    width: "95%",
    height: 80,
    backgroundColor: "#fff",
    borderRadius: 5,
    elevation: 5,
    margin: 10,
  },
  pill: {
    flex: 2,
    backgroundColor: "lightgreen",
    padding: 5,
    borderRadius: 5,
    maxWidth: 65,
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    marginRight: 20,
  },
  dateStyle: {
    flex: 3,
    fontSize: 12,
    color: "#777",
    marginLeft: 15,
    marginTop: 3,
  },
  jarNameContainer: { flexDirection: "row", marginVertical: 30 },
  jarText: { flex: 0.6, marginLeft: 20, fontWeight: "bold" },
});

export default History;
