import React, { Component } from "react";
import {
  StyleSheet,
  FlatList,
  View,
  Text,
  TouchableOpacity,
} from "react-native";

export default class History extends Component {
  constructor(props) {
    super(props);
    this.state = {
      arrayData: "",
      loading: true,
      edit: false,
    };
  }

  componentDidMount() {
    this.fetchData();
    this.setState({ loading: false });
  }

  fetchData() {
    var jarName = this.props.route.params.key;
    console.log(jarName);
    this.setState({ loading: true });
    fetch("http://192.168.18.3:3001/history/" + jarName)
      .then((res) => res.json())
      .then((resJson) => {
        console.log(resJson);
        this.setState({ arrayData: [...this.state.arrayData, resJson] });
        this.setState({ loading: false });
      })
      .catch((e) => console.log(e));
  }

  getDateTime() {
    const timeElapsed = Date.now();
    const today = new Date(timeElapsed);
  }

  handleRefresh = () => {
    this.setState({ loading: false }, () => {
      this.fetchData();
      console.log(this.state.arrayData);
    });
  };

  renderSeparator = () => (
    <View
      style={{
        marginBottom: -10,
      }}
    />
  );

  render() {
    return (
      <FlatList
        data={this.state.arrayData}
        renderItem={({ item, index }) => (
          <TouchableOpacity style={styles.card}>
            <View style={{ flexDirection: "row", marginVertical: 30 }}>
              <Text style={{ flex: 1, marginLeft: 20 }}>{index + 1}</Text>
              <Text style={{ flex: 2, fontSize: 16 }}>
                {this.getDateTime()}
              </Text>
              <View style={styles.pill}>
                <Text>{item.status}</Text>
              </View>
            </View>
          </TouchableOpacity>
        )}
        ItemSeparatorComponent={this.renderSeparator}
        keyExtractor={(item) => item.status}
        refreshing={this.state.loading}
        onRefresh={this.handleRefresh}
      />
    );
  }
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: "center",
    backgroundColor: "#f2f2f2",
    width: "100%",
    height: "100%",
  },
  card: {
    width: "95%",
    height: 80,
    backgroundColor: "#fff",
    borderRadius: 5,
    elevation: 5,
    margin: 10,
  },
  pill: {
    flex: 2,
    backgroundColor: "lightgreen",
    padding: 5,
    borderRadius: 5,
    maxWidth: 60,
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    marginRight: 20,
  },
});
