import React, { useState } from "react";
import {
  View,
  Text,
  TextInput,
  StyleSheet,
  TouchableOpacity,
  ToastAndroid,
} from "react-native";

const Signup = (props) => {
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [phone, setPhone] = useState("");
  const [password, setPassword] = useState("");
  const [jars, setJars] = useState(0);
  const [jarname, setJarname] = useState("");
  const [jarnamesArray, setJarnamesArray] = useState([]);

  const AdminCred = async (props) => {
    console.log("1");
    fetch("http://192.168.18.3:3001/adminSignup", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({
        name: name,
        email: email,
        contact: phone,
        password: password,
        jars: jarnamesArray,
      }),
    })
      .then((res) => res.json())
      .then(async (data) => {
        try {
          console.log("2");
          console.log(data);
          props.navigation.navigate("Admin Login");
          ToastAndroid.show("Signed up successfully !", ToastAndroid.SHORT);
        } catch (e) {
          console.log(e);
        }
      });
  };

  const addJar = () => {
    setJarnamesArray((jarnamesArray) => {
      return [...jarnamesArray, jarname];
    });
    ToastAndroid.show("item added", ToastAndroid.SHORT);
    setJars(jars + 1);
    setJarname("");
  };

  return (
    <View style={styles.container}>
      <Text style={styles.logo}>AUTO GROCERY</Text>
      <View style={styles.inputView}>
        <TextInput
          style={styles.inputText}
          placeholder="UserName..."
          placeholderTextColor="#003f5c"
          value={name}
          onChangeText={(text) => setName(text)}
        />
      </View>
      <View style={styles.inputView}>
        <TextInput
          style={styles.inputText}
          placeholder="Email..."
          placeholderTextColor="#003f5c"
          keyboardType="email-address"
          value={email}
          onChangeText={(text) => setEmail(text)}
        />
      </View>
      <View style={styles.inputView}>
        <TextInput
          style={styles.inputText}
          placeholder="Contact No..."
          placeholderTextColor="#003f5c"
          keyboardType="phone-pad"
          value={phone}
          onChangeText={(text) => setPhone(text)}
        />
      </View>
      <View style={styles.inputView}>
        <TextInput
          style={styles.inputText}
          placeholder="Password..."
          secureTextEntry={true}
          placeholderTextColor="#003f5c"
          value={password}
          onChangeText={(text) => setPassword(text)}
        />
      </View>
      <View style={styles.quantity}>
        <Text style={{ color: "#fff" }}>Jar Quantity: {jars}</Text>
      </View>
      <View style={styles.jarnamesView}>
        <View style={styles.jarnameInputView}>
          <TextInput
            style={styles.inputText}
            placeholder="Jar Name"
            placeholderTextColor="#003f5c"
            value={jarname}
            onChangeText={(text) => setJarname(text)}
          />
        </View>
        <TouchableOpacity style={styles.jarBtn}>
          <Text onPress={() => addJar()} style={styles.jarLabel}>
            ADD
          </Text>
        </TouchableOpacity>
      </View>
      <TouchableOpacity
        onPress={() => AdminCred(props)}
        style={styles.loginBtn}
      >
        <Text style={styles.loginText}>SIGNUP</Text>
      </TouchableOpacity>
      <TouchableOpacity
        onPress={() => props.navigation.navigate("Admin Login")}
      >
        <Text style={styles.signup}>Login</Text>
      </TouchableOpacity>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "#003f5c",
    alignItems: "center",
    justifyContent: "center",
  },
  logo: {
    fontWeight: "bold",
    fontSize: 45,
    color: "#fb5b5a",
    marginBottom: 40,
  },
  inputView: {
    width: "80%",
    backgroundColor: "#465881",
    borderRadius: 25,
    height: 50,
    marginBottom: 20,
    justifyContent: "center",
    padding: 20,
  },
  jarnameInputView: {
    width: "74%",
    backgroundColor: "#465881",
    borderRadius: 5,
    height: 50,
    marginBottom: 20,
    justifyContent: "center",
    padding: 20,
  },
  inputText: {
    height: 50,
    color: "white",
  },
  forgot: {
    color: "white",
    fontSize: 11,
  },
  signup: {
    color: "white",
    fontSize: 15,
  },
  loginBtn: {
    width: "80%",
    backgroundColor: "#fb5b5a",
    borderRadius: 25,
    height: 50,
    alignItems: "center",
    justifyContent: "center",
    marginTop: 40,
    marginBottom: 10,
  },
  jarBtn: {
    width: "20%",
    backgroundColor: "#fb5b5a",
    height: 50,
    alignItems: "center",
    borderRadius: 5,
  },
  jarnamesView: {
    width: "80%",
    display: "flex",
    flexDirection: "row",
    justifyContent: "space-between",
  },
  jarLabel: {
    color: "#000",
    marginTop: 15,
  },
  quantity: {
    display: "flex",
    width: "80%",
    marginBottom: 10,
  },
});

export default Signup;
