import React, { Component } from "react";
import {
  StyleSheet,
  FlatList,
  View,
  Text,
  TouchableOpacity,
} from "react-native";

export default class History extends Component {
  constructor(props) {
    super(props);
    this.state = {
      arrayData: "",
      dateTimeArr: "",
      loading: true,
      edit: false,
    };
  }

  componentDidMount() {
    this.fetchData();
    this.setState({ loading: false });
  }

  fetchData() {
    var jarName = this.props.route.params.key;
    console.log(jarName);
    this.setState({ loading: true });
    fetch("http://192.168.18.3:3001/history/" + jarName)
      .then((res) => res.json())
      .then((resJson) => {
        console.log(resJson);
        var values = resJson.status;
        this.setState({
          arrayData: [
            ...this.state.arrayData,
            { value: values, dateTime: new Date() },
          ],
        });
        this.setState({ loading: false });
      })
      .catch((e) => console.log(e));
  }

  getValues(val) {
    if (val >= 0 && val <= 10) {
      return <Text>high</Text>;
    } else if (val >= 11 && val <= 16) {
      return <Text>Medium</Text>;
    } else if (val >= 17) {
      return <Text>low</Text>;
    }
  }

  getDateTime(dt) {
    const dateTime = dt.toUTCString();
    return <Text style={{ flex: 3 }}>{dateTime}</Text>;
  }

  handleRefresh = () => {
    this.setState({ loading: false }, () => {
      this.fetchData();
      this.setState({ loading: true });
    });
  };

  renderSeparator = () => (
    <View
      style={{
        marginBottom: -10,
      }}
    />
  );

  render() {
    return (
      <FlatList
        data={this.state.arrayData}
        renderItem={({ item, index }) => (
          <TouchableOpacity style={styles.card}>
            <View style={{ flexDirection: "row", marginVertical: 30 }}>
              <Text style={{ flex: 0.6, marginLeft: 20 }}>{index + 1})</Text>
              {this.getDateTime(item.dateTime)}
              <View style={styles.pill}>{this.getValues(item.value)}</View>
            </View>
          </TouchableOpacity>
        )}
        ItemSeparatorComponent={this.renderSeparator}
        keyExtractor={(item, index) => index.toString()}
        refreshing={this.state.loading}
        onRefresh={this.handleRefresh}
      />
    );
  }
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: "center",
    backgroundColor: "#f2f2f2",
    width: "100%",
    height: "100%",
  },
  card: {
    width: "95%",
    height: 80,
    backgroundColor: "#fff",
    borderRadius: 5,
    elevation: 5,
    margin: 10,
  },
  pill: {
    flex: 2,
    backgroundColor: "lightgreen",
    padding: 5,
    borderRadius: 5,
    maxWidth: 65,
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    marginRight: 20,
  },
});
